if (msg.statusCode != 200) {

    throw 'response status ' + msg.statusCode + ' from Hue API'

}

const resources = flow.get('resources') || {}
const events = []

for (let resource of msg.payload.data) {

    const type = resources[resource.type] || {}

    type[resource.id] = resource
    resources[resource.type] = type

}

flow.set('resources', resources)

for (let id in resources.grouped_light) {

    const grouped_light = resources.grouped_light[id]

    events.push({
        topic: 'add',
        payload: grouped_light
    })
}

return [
    events,
    {
        payload: true
    }
]

