const merged = flow.get('merged') || []
const resources = flow.get('resources') || {}
const model = []

for (let entry of merged) {

    const grouped_light = entry.grouped_light
    const group = entry.group
    const scenes = entry.scenes
    const label = (group.metadata ? group.metadata.name : 'All Lights')

    model.push({
        label: label,
        id: grouped_light.id,
        state: resources.grouped_light[grouped_light.id].on.on,
        scenes: scenes.map((scene) => {
            return {
                id: scene.id,
                name: scene.metadata.name
            }
        }).sort((a, b) => {

            return a.name.localeCompare(b.name)

        })
    })
}

msg.payload = model.sort((a, b) => {

    return a.label.localeCompare(b.label)

})

return msg

const resources = flow.get('resources') || {}
const type = resources[msg.payload.type] || {}
const resource = type[msg.payload.id] || {}

function findScenes(group) {

    const scenes = []

    for (let id in resources.scene) {

        const scene = resources.scene[id]

        if (scene.group.rid == group.id) {

            scenes.push(scene)

        }
    }

    return scenes

}

function findGroup(grouped_light) {

    const groups = resources[grouped_light.owner.rtype]

    for (let id in groups) {

        const group = groups[id]

        if (id == grouped_light.owner.rid) {

            return {
                grouped_light: grouped_light,
                group: group,
                scenes: findScenes(group)
            }
        }
    }

    throw 'no group found for grouped_light ' + grouped_light.id

}

if (resource.type == 'grouped_light') {

    const merged = flow.get('merged') || []

    merged.push(findGroup(resource))
    flow.set('merged', merged)

}

