let fill = 'green'

try {

    if ((typeof msg.statusCode) != 'number') {

        fill = 'red'
        return msg

    }

    if (msg.statusCode < 200) {

        fill = 'red'
        return msg

    }

    if (msg.statusCode >= 300) {

        fill = 'red'
        return msg

    }

    if (!msg.payload.data) {

        fill = 'yellow'
        return msg

    }

    if (!msg.payload.errors) {

        fill = 'yellow'
        return msg

    }

    /*
    hue bridge api defect: meaningless warning messages
                           are often included in errors
                           array so there is no actual
                           way to determine if a command
                           was successful

    if (msg.payload.errors.length > 0) {

        fill = 'yellow'
        return msg

    }
    */

    if (msg.statusCode != 200) {

        fill = 'yellow'

    }

    return null

} finally {

    node.status({
        text: msg.statusCode,
        shape: 'dot',
        fill: fill
    })
}

