const MILLIS_PER_SECOND = 1000
const MILLIS_PER_MINUTE = 60 * MILLIS_PER_SECOND
const MILLIS_PER_HALF_HOUR = 30 * MILLIS_PER_MINUTE
const MILLIS_PER_HOUR = 2 * MILLIS_PER_HALF_HOUR

const bedtime = flow.get('bedtime', 'file') || 22
const date = new Date(msg.payload.time)
const time = new Date(date.getFullYear(), date.getMonth(), date.getDate(), bedtime, 0, 0, 0).getTime()
const offset = MILLIS_PER_HALF_HOUR - Math.floor(Math.random() * MILLIS_PER_HOUR)

msg.payload.bedtime = time + offset

node.status({
    text: new Date(msg.payload.bedtime).toLocaleTimeString(),
    shape: 'dot',
    fill: 'blue'
})

return msg

