/*global suncalc*/

const LATITUDE = Number.parseFloat(env.get('LATITUDE'))
const LONGITUDE = Number.parseFloat(env.get('LONGITUDE'))

const now = msg.payload.now
const daily = msg.payload.daily
const lighting = flow.get('lighting', 'file') || false
const shades = flow.get('shades', 'file') || false
const position = suncalc.getPosition(new Date(now), LATITUDE, LONGITUDE)

msg.topic = 'trigger'
msg.payload = {
    lighting: lighting,
    shades: shades,
    theme: daily.theme
}

if ((now < daily.sunrise) || (now >= daily.bedtime)) {

    msg.payload.time = 'night'

} else if (now > daily.sunset) {

    msg.payload.time = 'evening'

} else if (position.azimuth > 0) {

    if (position.altitude < 0.8) {

        msg.payload.time = 'afternoon'

    } else {

        msg.payload.time = 'midday'

    }

} else {

    if (position.altitude > 0.8) {

        msg.payload.time = 'midday'

    } else {

        msg.payload.time = 'morning'

    }
}

node.status({
    text: msg.payload.time,
    shape: 'dot',
    fill: 'blue'
})

return msg

