msg.dashboard = []

function findScenes(roomId) {

    const scenes = []

    for (const scene of msg.payload.scenes) {

        if (scene.roomId == roomId) {

            scenes.push(scene)

        }
    }

    return scenes.sort((a, b) => {
        return a.sceneName.localeCompare(b.sceneName)
    })
}

for (const room of msg.payload.rooms) {

    const scenes = findScenes(room.roomId)

    if (scenes.length > 0) {

        const roomScenes = []

        for (const scene of scenes) {

            roomScenes.push({
                sceneName: scene.sceneName,
                sceneId: scene.sceneId
            })
        }

        msg.dashboard.push({
            roomName: room.roomName,
            roomScenes: roomScenes
        })
    }
}

msg.dashboard = msg.dashboard.sort((a, b) => {
    return a.roomName.localeCompare(b.roomName)
})

context.set('metadata', msg.payload)
return msg

function toLocaleString(value) {

    if (value instanceof Date) {

        return value.toLocaleString()

    }

    if (Number.isInteger(value)) {

        return new Date(value).toLocaleString()

    }

    if (Array.isArray(value)) {

        return value.map(toLocaleString)

    }

    if ((typeof value) == 'object') {

        const result = {}

        for (let key in value) {

            result[key] = toLocaleString(value[key])

        }

        return result

    }

    return value

}

msg.payload = toLocaleString(msg.payload)
return msg
