if (!Number.isInteger(msg.statusCode)) {

    return msg

}

// ignore 207 due to frequent, spurious use by the Philips developers
if ((msg.statusCode != 200) && (msg.statusCode != 207)) {

    return msg

}

if (!Object.prototype.hasOwnProperty.call(msg.payload, 'data')) {

    return msg

}

if (!Object.prototype.hasOwnProperty.call(msg.payload, 'errors')) {

    return msg

}

if ((msg.statusCode != 207) && (msg.payload.errors.length > 0)) {

    return msg

}

return null

const TOPIC = env.get('TOPIC') || 'hue'
const resources = flow.get('resources')
const data = JSON.parse(msg.payload.data)

const messages = []

for (let datum of data) {

    for (let value of datum.data) {

        const message = RED.util.cloneMessage(msg)
        const category = resources[value.type] || {}
        let resource = category[value.id] || {}

        switch (datum.type) {

            case 'add':
            case 'update':

                for (let property in value) {

                    resource[property] = value[property]
                }

                category[value.id] = message.payload = resource
                break

            case 'delete':

                delete category[value.id]
                message.payload = ''
                break

            default:

                node.warn(JSON.stringify(datum, undefined, 1))
                break

        }

        resources[value.type] = category
        message.topic = TOPIC + '/resource/' + value.type + '/' + value.id
        message.retain = true
        messages.push(message)

    }
}

flow.set('resources', resources)
msg.payload = resources
return [msg, messages]

const model = []

function findOwner(grouped_light) {

    const groups = msg.payload[grouped_light.owner.rtype]

    if (Object.prototype.hasOwnProperty.call(groups, grouped_light.owner.rid)) {

        return groups[grouped_light.owner.rid]

    }

    throw 'no container found for grouped_light ' + grouped_light.id

}

function findScenes(owner) {

    const scenes = []

    for (let sceneId in msg.payload.scene) {

        const scene = msg.payload.scene[sceneId]

        if (scene.group.rid == owner.id) {

            scenes.push(scene)

        }
    }

    return scenes.sort((a, b) => {
        return a.metadata.name.localeCompare(b.metadata.name)
    })

}

for (let grouped_light_id in msg.payload.grouped_light) {

    const grouped_light = msg.payload.grouped_light[grouped_light_id]
    const owner = findOwner(grouped_light)
    const scenes = findScenes(owner)

    model.push({
        grouped_light: grouped_light,
        owner: owner,
        scenes: scenes,
        name: (owner.metadata ? owner.metadata.name : 'All Lights')
    })

}

msg.payload = model.sort((a, b) => {
    return a.name.localeCompare(b.name)
})

return msg

const messages = []

for (let command of env.get('COMMANDS')) {

    const message = RED.util.cloneMessage(msg)

    message.payload = command.payload
    message.topic = command.topic
    message.method = command.method
    message.target = command.target
    messages.push(message)

}

return [messages]

const date = new Date(msg.payload.daily)
const month = date.getMonth() + 1
const day = date.getDate()

if ((month == 7) && (day <= 4)) {

    msg.payload = 'tribal'

} else if (month == 10) {

    msg.payload = 'spooky'

} else if (month == 12) {

    msg.payload = 'jolly'

} else {

    msg.payload = 'standard'

}

node.status({
    text: msg.payload,
    shape: 'dot',
    fill: 'blue'
})

msg.topic = 'daily/theme'
return msg

/*global suncalc*/

const LATITUDE = Number.parseFloat(env.get('LATITUDE'))
const LONGITUDE = Number.parseFloat(env.get('LONGITUDE'))

const sunrise = RED.util.cloneMessage(msg)
const sunset = RED.util.cloneMessage(msg)
const date = new Date(msg.payload.daily)

const times = suncalc.getTimes(
    // work around bug in suncalc
    new Date(
        date.getFullYear(),
        date.getMonth(),
        date.getDate(),
        1,
        1
    ),
    LATITUDE,
    LONGITUDE
)

sunrise.payload = times.sunrise.getTime()
sunrise.topic = 'daily/sunrise'
sunset.payload = times.sunset.getTime()
sunset.topic = 'daily/sunset'

const status =
    new Date(sunrise.payload).toLocaleTimeString() +
    ' | ' +
    new Date(sunset.payload).toLocaleTimeString()

node.status({
    text: status,
    shape: 'dot',
    fill: 'blue'
})

return [sunrise, sunset]

const MILLIS_PER_SECOND = 1000
const MILLIS_PER_MINUTE = 60 * MILLIS_PER_SECOND
const MILLIS_PER_HALF_HOUR = 30 * MILLIS_PER_MINUTE
const MILLIS_PER_HOUR = 2 * MILLIS_PER_HALF_HOUR

const date = new Date(msg.payload.daily)
const offset = MILLIS_PER_HALF_HOUR - Math.floor(Math.random() * MILLIS_PER_HOUR)
const time = new Date(
    date.getFullYear(),
    date.getMonth(),
    date.getDate(),
    msg.payload['settings/bedtime']
).getTime()

msg.payload = time + offset

node.status({
    text: new Date(msg.payload).toLocaleTimeString(),
    shape: 'dot',
    fill: 'blue'
})

msg.topic = 'daily/bedtime'
return msg

/*global suncalc*/

const LATITUDE = Number.parseFloat(env.get('LATITUDE'))
const LONGITUDE = Number.parseFloat(env.get('LONGITUDE'))

const now = msg.payload.now
const sunrise = msg.payload['daily/sunrise']
const sunset = msg.payload['daily/sunset']
const bedtime = msg.payload['daily/bedtime']
const position = suncalc.getPosition(new Date(now), LATITUDE, LONGITUDE)

if ((now < sunrise) || (now >= bedtime)) {

    msg.payload = 'night'

} else if (now > sunset) {

    msg.payload = 'evening'

} else if (position.altitude > 0.8) {

    msg.payload = 'midday'

} else if (position.azimuth > 0) {

    msg.payload = 'afternoon'

} else {

    msg.payload = 'morning'

}

node.status({
    text: msg.payload,
    shape: 'dot',
    fill: 'blue'
})

msg.topic = 'timer/time'
return msg

let fill = 'green'

try {

    if ((typeof msg.statusCode) != 'number') {

        fill = 'red'
        return msg

    }

    if ((msg.statusCode < 200) || (msg.statusCode >= 300)) {

        fill = 'red'
        return msg

    }

    if (msg.statusCode != 200) {

        fill = 'yellow'

    }

    return null

} finally {

    node.status({
        text: msg.statusCode,
        shape: 'dot',
        fill: fill
    })
}

msg.dashboard = []

function findScenes(roomId) {

    const scenes = []

    for (const scene of msg.payload.scenes) {

        if (scene.roomId == roomId) {

            scenes.push(scene)

        }
    }

    return scenes.sort((a, b) => {
        return a.name.localeCompare(b.name)
    })
}

for (const room of msg.payload.rooms) {

    const scenes = findScenes(room.id)

    if (scenes.length > 0) {

        const roomScenes = []

        for (const scene of scenes) {

            roomScenes.push({
                sceneName: scene.name,
                sceneId: scene.id
            })
        }

        msg.dashboard.push({
            roomName: room.name,
            roomScenes: roomScenes
        })
    }
}

msg.dashboard = msg.dashboard.sort((a, b) => {
    return a.roomName.localeCompare(b.roomName)
})

return msg

const messages = []

for (let address of msg.payload.addresses) {

    const message = RED.util.cloneMessage(msg)

    message.payload = {
        id: msg.payload.txtRecord.bridgeid,
        model: msg.payload.txtRecord.modelid,
        name: msg.payload.name,
        host: msg.payload.host,
        address: address,
        port: msg.payload.port
    }

    message.topic = 'hue/bridge/' + message.payload.id
    messages.push(message)

}

return [messages]
